<?php

function getNominalRefund($konek, $dataJamaah){
	$JAMAAH	=	$dataJamaah;
	$sql	=	"SELECT * FROM tbl_pembayaran where no_jamaah='".$JAMAAH['noreg_jamaah']."' and pembayaran='refund' order by id_Pay desc";
	$query	=	mysqli_query($konek,$sql);
	$data	=	mysqli_fetch_array($query);
	return $data;
}
function getDataPembayaranRefund($konek, $dataJamaah){
	$sql="select * from tbl_pembayaran as p where p.no_jamaah='".$dataJamaah['noreg_jamaah']."' and p.pembayaran='Refund' order by p.id_pay desc";
	$query	=	mysqli_query($konek,$sql);
	$data	=	mysqli_fetch_array($query);
	return $data;
}

